// Minimal placeholder for leaflet-sidepanel.min.js. Replace with official plugin JS for full functionality.
L.Control.SidePanel = L.Control.extend({
  options: {
    panelPosition: 'right',
    hasTabs: true,
    tabsPosition: 'top',
    pushControls: true,
    darkMode: false,
    startTab: 'tab-1'
  },
  initialize: function(panelId, options) {
    L.setOptions(this, options);
    this.panelId = panelId;
  },
  onAdd: function(map) {
    var panel = document.getElementById(this.panelId);
    if (panel) {
      panel.style[this.options.panelPosition] = '0px';
      panel.setAttribute('aria-hidden', 'false');
    }
    return L.DomUtil.create('div');
  }
});
L.control.sidepanel = function(panelId, options) {
  return new L.Control.SidePanel(panelId, options);
};
